// Build Followup Text (SP3)
// - garante que telefone exista
// - monta followup_base_text
// - anexa followup_settings (do node Load Followup Settings)
// - mant√©m tudo no MESMO item para o agente e para o envio

// 1) pega o item "due" vindo do Claim Due Followups
const due = $json || {};

// 2) pega settings do node Load Followup Settings (primeiro item)
const settingsNode = $items("Load Followup Settings")[0];
const settings = settingsNode?.json || {};

// 3) normaliza telefone
const telefone = String(
  due.telefone ?? due.remoteJid ?? due.number ?? ""
)
  .replace("@s.whatsapp.net", "")
  .replace("@c.us", "")
  .trim();

// 4) texto base (pr√©-IA) ‚Äî usa o que veio do banco, com fallback
const baseText = String(
  due.followup_base_text ?? "Oi! Passando rapidinho por aqui üôÇ"
).trim();

// 5) aqui voc√™ pode enriquecer com contexto/abertura padr√£o
// (mantive simples para n√£o quebrar seu fluxo)
const textoFinal = baseText;

// 6) retorna 1 item consolidado
return [
  {
    json: {
      ...due,
      telefone,
      followup_base_text: textoFinal,
      followup_settings: settings,
    },
  },
];

