const results = [];
for (const inputItem of validItems) {
  const item = inputItem.json;
  const stage = parseInt(item.followup_stage || 0);
  const telefone = String(item.telefone).replace(/\D/g, '');
  if (!telefone) continue;

  const msg1 = item.msg_1 || 'Oi! Passando para ver se conseguiu ler minha ultima mensagem? \ud83d\udc40';
  const msg2 = item.msg_2 || 'Ainda por ai? Se preferir, podemos marcar um papo rapido para eu tirar suas duvidas! \ud83d\udcf2';
  const msg3 = item.msg_3 || 'Vi que as coisas devem estar corridas! Vou deixar nosso link de agenda aqui para quando voce puder. \ud83e\udd1d';

  let mensagem = '';
  let next_stage = 1;

  if (stage === 0) { mensagem = msg1; next_stage = 1; }
  else if (stage === 1) { mensagem = msg2; next_stage = 2; }
  else { mensagem = msg3; next_stage = 3; }

  results.push({
    json: {
      ...item,
      telefone,
      mensagem,
      next_stage,
      evo_api_url: item.evo_api_url || 'https://evo.sp3company.shop',
      instance_name: item.instance_name || 'v1'
    }
  });
}

return results.length > 0 ? results : [{ json: { _skip: true } }];

